            }
            
            it("can encode dates") {
                expect(NSDate(timeIntervalSinceReferenceDate: 265336576).toDER()) == [0x18, 0x0F] + [UInt8]("20090530003616Z".utf8)
            }
            
            it("can encode simple sequence") {
                let sequence = DERSequence { 72.toDER() + true.toDER() }
                expect(sequence.toDER()) == [0x30, 0x06,  0x02, 0x01, 0x48,  0x01, 0x01, 0xFF]
                
                let sequence2 = DERSequence { 42.toDER() + "hello world".toDER() + false.toDER() }
                expect(sequence2.toDER()) == [0x30, 0x13, 0x02, 0x01, 0x2A, 0x0C, 0x0B, 0x68, 0x65, 0x6C, 0x6C, 0x6F, 0x20, 0x77, 0x6F, 0x72, 0x6C, 0x64, 0x01, 0x01, 0x00]
            }
            
            it("can encode nested sequence") {
                let sequence = DERSequence {
                    DERSequence { 72.toDER() + true.toDER() }.toDER() +
                    DERSequence { 72.toDER() + true.toDER() }.toDER()
                }
                expect(sequence.toDER()) == [0x30, 0x10,
                                             0x30, 0x06,  0x02, 0x01, 0x48,  0x01, 0x01, 0xFF,
                                             0x30, 0x06,  0x02, 0x01, 0x48,  0x01, 0x01, 0xFF]
            }
            
            describe("OID") {
                it("can encode empty OID") {
                    expect(OID(components:[]).toDER()) == []
                }
                
                it("collapses first two bytes if required") {
                    expect(OID(components:[0,2]).toDER()) == [0x02]
                    expect(OID(components:[1,2]).toDER()) == [0x2a]
                    expect(OID(components:[2,3]).toDER()) == [0x53]
                    expect(OID(components:[4,1]).toDER()) != [0xA1]
                    expect(OID(components:[1,42]).toDER()) != [0x52]
                }
                
                it("can encode OID 1") {
                    let oidComponents : [UInt32] = [1,2,840,113549,1,1,1]
                    let oid = OID(components:oidComponents)
                    expect(oid.toDER()) == [0x2a, 0x86, 0x48, 0x86,  0xf7, 0x0d, 0x01, 0x01,  0x01]
                }
                
                it("can encode OID 2") {
                    let oidComponents : [UInt32] = [2,5,4,4]
                    let oid = OID(components:oidComponents)
                    expect(oid.toDER()) == [0x55,0x04,0x04]
                }
                
                it("can encode OID 3") {
                    expect(OID(components: [1, 2, 840, 113549, 1, 9, 1]).toDER()) == [0x2a,0x86,0x48,0x86,0xf7,0x0d,0x01,0x09,0x01]
                }
            }
        }
        
        describe("Leading zeros") {
            it ("can remove leading zeros") {
                expect([UInt8]().removeLeadingZeros()) == []
                expect([0].removeLeadingZeros()) == [0]
                expect([1].removeLeadingZeros()) == [1]
                expect([0,0].removeLeadingZeros()) == [0]
                expect([1,0].removeLeadingZeros()) == [1,0]
                expect([0,1].removeLeadingZeros()) == [1]
                expect([1,1].removeLeadingZeros()) == [1,1]
            }
            
            it ("can compress leading 0xFF") {
                expect([UInt8]().ensureSingleLeadingOneBit()) == []
                expect([0].ensureSingleLeadingOneBit()) == [0xFF, 0]
                expect([1].ensureSingleLeadingOneBit()) == [0xFF, 1]
                expect([0xFF].ensureSingleLeadingOneBit()) == [0xFF]
                expect([0xFF,0xFF].ensureSingleLeadingOneBit()) == [0xFF]
                expect([0xFF,0xFF,0x00].ensureSingleLeadingOneBit()) == [0xFF,0x00]
                expect([0x80].ensureSingleLeadingOneBit()) == [0x80]
                expect([0x40].ensureSingleLeadingOneBit()) == [0xFF,0x40]
            }
        }
